    function getJSON(url, data, callback, errorCb) {
        if (data != null) {
            var params = [];
            for (var k in data) {
                params.push(k + "=" + encodeURI(data[k]));
            }
            url += "?" + params.join("&");
        }

        WinJS.xhr(
            {
                url: url,
                responseType: 'json'
            }
        ).then(
            function (r) {
                if (callback != null)
                    callback(r.response, r.status);
            },
            function (r) {
                if (errorCb)
                    errorCb(r.status);
            }
        );
    }

